$(document).ready(function(){  	var altura 		 = screen.height; 	$(".painel").css({'min-height':altura}); 	 	$('.painel').each(function(){			 		var $obj = $(this); 		$(window).scroll(function() { 			var yPos = -($(window).scrollLeft() / $obj.data('speed'));  		}); 	}); 	$(window).scroll(function() {         var fullScroll = $(this).scrollTop(); 	    if(fullScroll >= 50)         {             $('section').each(function(i) {                 if (($(this).position().top - 150) <= fullScroll) {                     $('.itemmenu').parent().removeClass('selectedmenu');                     $('.itemmenu').eq(i).parent().addClass('selectedmenu');                 }             });         }         else         {             $('.itemmenu').parent().removeClass('selectedmenu');             $('.itemmenu:first').parent().addClass('selectedmenu');         }      });  });